import React from "react";
import { motion } from "framer-motion";

const containerVariants = {
  hidden: {
    opacity: 0,
    x: "100vw",
  },
  visible: {
    opacity: 1,
    x: 0,
    transition: {
      type: "spring",
      stiffness: 70,
      // delay: 0.5,
      mass: 0.4,
      damping: 8,
      when: "beforeChildren", //animates before the children animations
      staggerChildren: 0.4, // staggers the children each with a separation of  0.4 seconds
    },
  },
};

const childVariants = { 
  hidden: {
    opacity: 0,
  },
  visible: {
    opacity: 1,
  },
};
const Order = ({ pizza }) => {

  const [showTitle, setsShowTitle] = useState(initialState)

  setTimeout(() => {
  setsShowTitle(false)  
  }, 4000);

  return (
    <motion.div
      className="container order"
      variants={containerVariants}
      initial="hidden"
      animate="visible"
    >
      {showTitle && (
        <h2>Thank you for your order :)</h2>

      )}
      <motion.p variants={childVariants}>
        You ordered a {pizza.base} pizza with:
      </motion.p>

      <motion.div variants={childVariants}>
        {pizza.toppings.map((topping) => (
          <div key={topping}>{topping}</div>
        ))}
      </motion.div>
    </motion.div>
  );
};

export default Order;
